'use strict';

var $TypeError = require('es-errors/type');
import isObject from 'es-object-atoms/isObject';

import isPropertyDescriptor from '../helpers/records/property-descriptor';
import DefineOwnProperty from '../helpers/DefineOwnProperty';

import FromPropertyDescriptor from './FromPropertyDescriptor';
import IsDataDescriptor from './IsDataDescriptor';
import isPropertyKey from '../helpers/isPropertyKey';
import SameValue from './SameValue';
import ToPropertyDescriptor from './ToPropertyDescriptor';

// https://262.ecma-international.org/6.0/#sec-definepropertyorthrow

module.exports = function DefinePropertyOrThrow(O, P, desc) {
	if (!isObject(O)) {
		throw new $TypeError('Assertion failed: Type(O) is not Object');
	}

	if (!isPropertyKey(P)) {
		throw new $TypeError('Assertion failed: P is not a Property Key');
	}

	var Desc = isPropertyDescriptor(desc) ? desc : ToPropertyDescriptor(desc);
	if (!isPropertyDescriptor(Desc)) {
		throw new $TypeError('Assertion failed: Desc is not a valid Property Descriptor');
	}

	return DefineOwnProperty(
		IsDataDescriptor,
		SameValue,
		FromPropertyDescriptor,
		O,
		P,
		Desc
	);
};
