'use strict';

var $TypeError = require('es-errors/type');

import callBound from 'call-bound';
import forEach from '../helpers/forEach';
import isLeadingSurrogate from '../helpers/isLeadingSurrogate';
import isTrailingSurrogate from '../helpers/isTrailingSurrogate';

var $charCodeAt = callBound('String.prototype.charCodeAt');
var $strSplit = callBound('String.prototype.split');

import UnicodeEscape from './UnicodeEscape';
import UTF16DecodeString from './UTF16DecodeString';
import UTF16Encoding from './UTF16Encoding';

import hasOwn from 'hasown';

// https://262.ecma-international.org/11.0/#sec-quotejsonstring

var escapes = {
	'\u0008': '\\b',
	'\u0009': '\\t',
	'\u000A': '\\n',
	'\u000C': '\\f',
	'\u000D': '\\r',
	'\u0022': '\\"',
	'\u005c': '\\\\'
};

module.exports = function QuoteJSONString(value) {
	if (typeof value !== 'string') {
		throw new $TypeError('Assertion failed: `value` must be a String');
	}
	var product = '"';
	if (value) {
		forEach($strSplit(UTF16DecodeString(value), ''), function (C) {
			if (hasOwn(escapes, C)) {
				product += escapes[C];
			} else {
				var cCharCode = $charCodeAt(C, 0);
				if (cCharCode < 0x20 || isLeadingSurrogate(cCharCode) || isTrailingSurrogate(cCharCode)) {
					product += UnicodeEscape(C);
				} else {
					product += UTF16Encoding(cCharCode);
				}
			}
		});
	}
	product += '"';
	return product;
};
